import { useState } from 'react';
import './DashboardContainer.css';
import AgentCard from '../AgentCard';
import AgentDetails from '../AgentDetails';

const imgEllipse1 = "http://localhost:3845/assets/dcc912ce11c40eac810e7d22fb275abb31670d4b.svg";
const imgEllipse2 = "http://localhost:3845/assets/94538a1e7a646e4519925df62f18476b373c5bee.svg";
const imgEllipse3 = "http://localhost:3845/assets/721109041c9a688b03535beb6d7d05ad3685a5fa.svg";
const imgDropdownArrow = "http://localhost:3845/assets/5356d5889cbe72300bc2c08be7daa8190ccab606.svg";

function DashboardContainer() {
  const [selectedFilter, setSelectedFilter] = useState('All');
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [selectedAgent, setSelectedAgent] = useState(null);

  const agents = [
    { name: "Sarah Johnson", contactId: "1247685", specialization: "Customer Service", totalCases: 1247, avatar: imgEllipse1 },
    { name: "Mike Davis", contactId: "1247686", specialization: "Technical Support", totalCases: 892, avatar: imgEllipse3 },
    { name: "Emma Wilson", contactId: "1247687", specialization: "Sales", totalCases: 1456, avatar: imgEllipse2 },
    { name: "John Smith", contactId: "1247688", specialization: "Customer Service", totalCases: 1023, avatar: imgEllipse2 },
    { name: "Lisa Brown", contactId: "1247689", specialization: "Technical Support", totalCases: 734, avatar: imgEllipse1 },
    { name: "David Lee", contactId: "1247690", specialization: "Sales", totalCases: 1678, avatar: imgEllipse2 },
    { name: "Anna Garcia", contactId: "1247691", specialization: "Customer Service", totalCases: 945, avatar: imgEllipse3 },
    { name: "Tom Wilson", contactId: "1247692", specialization: "Technical Support", totalCases: 1234, avatar: imgEllipse1 },
    { name: "Kate Johnson", contactId: "1247693", specialization: "Sales", totalCases: 1567, avatar: imgEllipse1 }
  ];

  const specializations = ['All', 'Customer Service', 'Technical Support', 'Sales'];

  const filteredAgents = selectedFilter === 'All' 
    ? agents 
    : agents.filter(agent => agent.specialization === selectedFilter);

  const handleAgentClick = (agent) => {
    setSelectedAgent(agent);
  };

  const handleBackClick = () => {
    setSelectedAgent(null);
  };

  const handleFilterSelect = (filter) => {
    setSelectedFilter(filter);
    setIsDropdownOpen(false);
  };

  return (
    <div className="dashboard-page">
      {selectedAgent ? (
        <AgentDetails agent={selectedAgent} onBack={handleBackClick} />
      ) : (
        <>
          <div className="filter-dropdown">
            <button 
              className="filter-btn" 
              onClick={() => setIsDropdownOpen(!isDropdownOpen)}
            >
              <span>FILTER</span>
              <img src={imgDropdownArrow} alt="" className="dropdown-arrow" />
            </button>
            {isDropdownOpen && (
              <div className="dropdown-menu">
                {specializations.map(spec => (
                  <div
                    key={spec}
                    className={`dropdown-item ${selectedFilter === spec ? 'active' : ''}`}
                    onClick={() => handleFilterSelect(spec)}
                  >
                    {spec}
                  </div>
                ))}
              </div>
            )}
          </div>

          <div className="agents-grid">
            {filteredAgents.map((agent, index) => (
              <AgentCard key={index} agent={agent} onClick={handleAgentClick} />
            ))}
          </div>
        </>
      )}
    </div>
  );
}

export default DashboardContainer;
