import React, { useState, useEffect } from 'react';
import './PerformanceRubrics.css';

// Import icons
const imgWest = "http://localhost:3845/assets/dab477c6ade2d19b6d463e2d6994e32669d7440c.svg";
const imgWestIcon = "http://localhost:3845/assets/9fc49a8ad4582ec588abb9db25500f5811dbc74c.svg";
const imgHeadset = "http://localhost:3845/assets/a66be842bec527b13441dcf2743fcf4ff6738079.svg";
const imgHeadsetIcon = "http://localhost:3845/assets/3bec8d72294e72fa71bb179a6527aeceab51f3b7.svg";
const imgArrowDown = "http://localhost:3845/assets/dab477c6ade2d19b6d463e2d6994e32669d7440c.svg";

// Skill point definitions
const skillPoints = {
  "Rapport Skills": {
    "Tone": 1,
    "Professional": 1,
    "Conversational Style": 1,
    "Supportive Initial Statement": 1,
    "Affirmation and Praise": 1,
    "Reflection of Feelings": 2,
    "Explores Problem(s)": 1,
    "Values the Person": 1,
    "Non-Judgmental": 1
  },
  "Counseling Skills": {
    "Clarifies Non-Suicidal Safety": 1,
    "Suicide Safety Assessment (SSA/LRA) Initiation and Completion": 4,
    "Exploration of Buffers": 1,
    "Restates then Collaborates Options": 1,
    "Identifies a Concrete Plan": 2,
    "Appropriate Termination (Follow-Up Offered)": 1
  },
  "Organizational Skills": {
    "POP Model – Does Not Rush": 1,
    "POP Model – Does Not Dwell": 1
  },
  "Technical Skills": {
    "Greeting": 1,
    "SSA (LRA) Documentation": 1,
    "Call Documentation / SC Communication": 1
  }
};

// Calculate total possible points (multiplied by 4)
const calculateTotalPossiblePoints = () => {
  let total = 0;
  Object.keys(skillPoints).forEach(category => {
    Object.keys(skillPoints[category]).forEach(skill => {
      total += skillPoints[category][skill];
    });
  });
  return total * 4;
};

const TOTAL_POSSIBLE_POINTS = calculateTotalPossiblePoints();

function PerformanceRubrics({ fileName, s3Url, onBack }) {
  const [rubricData, setRubricData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [expandedSections, setExpandedSections] = useState({});

  const toggleSection = (sectionName) => {
    setExpandedSections(prev => ({
      ...prev,
      [sectionName]: !prev[sectionName]
    }));
  };

  useEffect(() => {
    const fetchRubricData = async () => {
      try {
        const response = await fetch(s3Url);
        const data = await response.json();
        setRubricData(data);
      } catch (error) {
        console.error('Error fetching rubric data:', error);
        setRubricData({
          totalMultipliedScore: 80,
          totalPossibleScore: TOTAL_POSSIBLE_POINTS,
          percentageScore: (80 / TOTAL_POSSIBLE_POINTS) * 100,
          criteria: "Meets Criteria",
          categories: {
            "RAPPORT SKILLS": {
              rawScore: 9,
              multipliedScore: 36,
              criteria: {
                "Tone": { score: 1, label: "Yes", observation: "Calm and supportive tone.", evidence: "00:08.528 AGENT: OK, thank you for giving us a call. What's going on?" },
                "Professional": { score: 1, label: "Yes", observation: "Conversation is appropriate and suitable for a Boys Town Crisis Counselor.", evidence: "00:01.470 AGENT: Vois National hotline, this is Pablo. How can I help you?" },
                "Conversational Style": { score: 1, label: "Yes", observation: "Balanced conversation with back and forth dialogue.", evidence: "00:05.049 CUSTOMER: Hello, I, I just need someone to talk to right now." }
              }
            },
            "COUNSELING SKILLS": {
              rawScore: 8,
              multipliedScore: 32,
              criteria: {
                "Clarifies Non-Suicidal Safety": { score: 1, label: "Yes", observation: "Asks appropriate clarifying questions about potential safety concerns.", evidence: "08:10.170 AGENT: Right. Now you mentioned a little bit about. Suicidal thoughts." },
                "Suicide Safety Assessment-SSA Initiation and Completion": { score: 4, label: "Yes", observation: "Conversationally asks the required SSA questions as listed in CMS.", evidence: "08:10.170 AGENT: Right. Now you mentioned a little bit about. Suicidal thoughts." }
              }
            },
            "ORGANIZATIONAL SKILLS": {
              rawScore: 2,
              multipliedScore: 8,
              criteria: {
                "POP Model - does not rush": { score: 1, label: "Yes", observation: "CC sufficiently explores and understands the problem prior to moving to Options and Plan.", evidence: "04:29.939 AGENT: What makes you think you won't be able to go to college?" }
              }
            },
            "TECHNICAL SKILLS": {
              rawScore: 1,
              multipliedScore: 4,
              criteria: {
                "Greeting": { score: 1, label: "Yes", observation: "Greeting is correct and pleasant.", evidence: "00:01.470 AGENT: Vois National hotline, this is Pablo. How can I help you?" }
              }
            }
          }
        });
      } finally {
        setLoading(false);
      }
    };

    fetchRubricData();
  }, [s3Url]);

  if (loading) {
    return <div className="performance-rubrics-loading">Loading...</div>;
  }

  return (
    <div className="performance-rubrics-container">
      <div className="performance-rubrics-header">
        <div className="back-button" onClick={onBack}>
          <div className="back-icon-container">
            <img src={imgWest} alt="" className="west-arrow" />
            <img src={imgWestIcon} alt="" className="west-icon" />
          </div>
        </div>
      </div>

      <div className="file-info-card">
        <div className="file-icon-container">
          <img src={imgHeadset} alt="" className="headset-icon" />
          <img src={imgHeadsetIcon} alt="" className="headset-icon-inner" />
        </div>
        <div className="file-name">{fileName}</div>
        <div className="overall-score">{Math.round(rubricData.percentageScore)} / 100</div>
        <div className="status-badge">
          <span className="status-text">{rubricData.criteria}</span>
        </div>
      </div>

      <div className="rubrics-card">
        <h2 className="rubrics-title">Detailed Performance Rubrics</h2>

        <div className="rubrics-list">
          {Object.entries(rubricData.categories).map(([categoryName, categoryData]) => {
            const percentage = (categoryData.multipliedScore / (Object.keys(categoryData.criteria).length * 4)) * 100;
            return (
              <div key={categoryName} className="rubric-item">
                <div className="rubric-header" onClick={() => toggleSection(categoryName)}>
                  <div className="rubric-left">
                    <img src={imgArrowDown} alt="" className={`expand-arrow ${expandedSections[categoryName] ? 'expanded' : ''}`} />
                    <h3 className="rubric-name">{categoryName}</h3>
                  </div>
                  <div className="rubric-score">{categoryData.multipliedScore} / {Object.keys(categoryData.criteria).length * 4}</div>
                </div>
                <div className="rubric-status">
                  <span className="status-text">Meets Criteria</span>
                </div>
                <div className="progress-bar">
                  <div className="progress-background"></div>
                  <div className="progress-fill" style={{ width: `${percentage}%` }}></div>
                </div>

                {expandedSections[categoryName] && (
                  <div className="criteria-details">
                    {Object.entries(categoryData.criteria).map(([criteriaName, criteriaData]) => (
                      <div key={criteriaName} className="criteria-item">
                        <div className="criteria-header">
                          <span className="criteria-name">{criteriaName}</span>
                          <span className="criteria-score">{criteriaData.score * 4}/{skillPoints[categoryName]?.[criteriaName] ? skillPoints[categoryName][criteriaName] * 4 : 4}</span>
                        </div>
                        <div className="criteria-observation">{criteriaData.observation}</div>
                        <div className="criteria-evidence">{criteriaData.evidence}</div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default PerformanceRubrics;
