import { useState, useRef } from 'react';
import './UploadContainer.css';

const imgHeadsetBase = "http://localhost:3845/assets/a66be842bec527b13441dcf2743fcf4ff6738079.svg";
const imgHeadsetMic = "http://localhost:3845/assets/3bec8d72294e72fa71bb179a6527aeceab51f3b7.svg";
const imgCloudUpload = "http://localhost:3845/assets/dab477c6ade2d19b6d463e2d6994e32669d7440c.svg";
const imgUpgrade = "http://localhost:3845/assets/2d0036a4673d40b1ba9724f1c380e9f17ffe6c49.svg";

function UploadContainer() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isDragOver, setIsDragOver] = useState(false);
  const fileInputRef = useRef(null);

  const validateFile = (file) => {
    const allowedTypes = ['audio/wav', 'audio/mpeg', 'audio/mp3'];
    const allowedExtensions = ['.wav', '.mp3'];
    const fileExtension = file.name.toLowerCase().substring(file.name.lastIndexOf('.'));

    return allowedTypes.includes(file.type) || allowedExtensions.includes(fileExtension);
  };

  const handleFileSelect = (file) => {
    if (validateFile(file)) {
      setSelectedFile(file);
    } else {
      alert('Please select a .wav or .mp3 file');
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    setIsDragOver(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragOver(false);
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      handleFileSelect(files[0]);
    }
  };

  const handleBrowseClick = () => {
    fileInputRef.current.click();
  };

  const handleFileInputChange = (e) => {
    if (e.target.files.length > 0) {
      handleFileSelect(e.target.files[0]);
    }
  };

  return (
    <div className="upload-container">
      <div className="headset-container">
        <div className="headset-icon">
          <img src={imgHeadsetBase} alt="" className="headset-base" />
          <img src={imgHeadsetMic} alt="" className="headset-mic" />
        </div>
      </div>
      <div className="upload-header">
        <h2>Upload Call Recording</h2>
      </div>

      <div 
        className={`upload-area ${isDragOver ? 'drag-over' : ''}`}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
        onClick={handleBrowseClick}
      >
        <div className="upload-circle">
          <img src={imgCloudUpload} alt="" className="cloud-icon" />
        </div>
        <p className="upload-text">
          {selectedFile ? selectedFile.name : 'Drag and Drop or click to browse'}
        </p>
        <p className="file-types">Supported file types - .wav, .mp3</p>

        <button className="browse-btn" onClick={handleBrowseClick}>
          <img src={imgUpgrade} alt="" className="upgrade-icon" />
          <span>Browse Files</span>
        </button>
      </div>

      <input
        ref={fileInputRef}
        type="file"
        accept=".wav,.mp3,audio/wav,audio/mpeg"
        onChange={handleFileInputChange}
        style={{ display: 'none' }}
      />

      <button className="upload-btn" disabled={!selectedFile}>
        Upload Recording
      </button>
    </div>
  );
}

export default UploadContainer;
